import React, { useState } from 'react';
import questions from './QuestionBox.jsx';
import score from './QuestionBox.jsx';


function FinalScore() {

    const finalScore = useState(score);

	const scoreText = useState(["The man from Utopia!", "Wild love", "Tryin’ To Grow A Chin",
			"Cosmik Debris", "Dancing fool", "Shut up and play your guitar", "Dumb all over"]);

    const scoreComment = (score) => {
    if (finalScore === 10) {
        return scoreText[0];
        }
    if (finalScore > 7 && score < 10) {
        return scoreText[1];
        }
    if (finalScore > 5 && score < 8) {
        return scoreText[2];
        }
    if (finalScore > 3 && score < 6) {
        return scoreText[3];
        }
    if (score > 1 && score < 4) {
        return scoreText[4];
        }
    if (finalScore === 1) {
        return scoreText[5];
        }
    if (finalScore === 0) {
        return  scoreText[6];
        }
    }

    return (
        <div>
            <div className='score-section'>					
                    You scored {score} out of {questions.length}					
            </div>
            <div className='score-section'>	
                {scoreComment(score)}
            </div>
        </div>
    );
}

export default FinalScore;
